DROP TABLE IF EXISTS `data_model_info`;
CREATE TABLE `data_model_info` (
  `id` int(11) NOT NULL auto_increment,
  `make` varchar(25) NOT NULL default '',
  `model` varchar(25) NOT NULL default '',
  `submodel` varchar(25) NOT NULL default '',
  `description` mediumtext NOT NULL,
  `videos` mediumtext NOT NULL,
  `images` mediumtext NOT NULL,
  `engine` varchar(25) NOT NULL default '',
  `displacement` varchar(25) NOT NULL default '',
  `horsepower` varchar(25) NOT NULL default '',
  `acceleration` varchar(25) NOT NULL default '',
  `topSpeed` varchar(25) NOT NULL default '',
  `msrp` varchar(25) NOT NULL default '',
  `brochure` varchar(100) NOT NULL default '',
  `configurator` varchar(100) NOT NULL default '',
  `manufacture` varchar(100) NOT NULL default '',
  `visible` tinyint(1) NOT NULL default '1',
  `domain` enum('www.bmcd.com','901.bmcd.com','999.bmcd.com') NOT NULL,
  `dt` datetime NOT NULL,
  `blnvalid` tinyint(1) NOT NULL default '1',
  PRIMARY KEY  (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=42 DEFAULT CHARSET=utf8;



