DROP TABLE IF EXISTS `data_store`;
CREATE TABLE IF NOT EXISTS `data_store` (
`id` INT NOT NULL AUTO_INCREMENT PRIMARY KEY ,
`productId` VARCHAR( 25 ) NOT NULL DEFAULT '',
`brand` VARCHAR( 25 ) NOT NULL DEFAULT '',
`productNumber` VARCHAR( 25 ) NOT NULL DEFAULT '',
`title` VARCHAR( 100 ) NOT NULL DEFAULT '',
`price` VARCHAR( 25 ) NOT NULL DEFAULT '',
`size` VARCHAR( 25 ) NOT NULL DEFAULT '',
`color` VARCHAR( 25 ) NOT NULL DEFAULT '',
`mfca` VARCHAR( 1 ) NOT NULL DEFAULT '',
`images` MEDIUMTEXT NOT NULL DEFAULT '',
`thumb` VARCHAR( 200 ) NOT NULL DEFAULT '',
`sale` VARCHAR( 25 ) NOT NULL DEFAULT '',
`shortDescription` VARCHAR( 500 ) NOT NULL DEFAULT '',
`longDescription` MEDIUMTEXT NOT NULL DEFAULT '',
`category` VARCHAR( 200 ) NOT NULL DEFAULT '',
`visible` BOOL NOT NULL DEFAULT  '1',
`domain` ENUM(  'www.bmcd.com',  '901.bmcd.com',  '999.bmcd.com' ) NOT NULL ,
`dt` DATETIME NOT NULL ,
`blnvalid` BOOL NOT NULL DEFAULT  '1'
) ENGINE = INNODB;



